/* Variables CSS para consistencia */
:root {
  --primary-color: #FF3737;
  --primary-hover: #e62e2e;
  --success-color: #28a745;
  --bg-light: #FCF5F5;
  --bg-lighter: #FFE2E2;
  --text-dark: #333;
  --text-light: #666;
  --shadow-sm: 0 2px 8px rgba(0, 0, 0, 0.1);
  --shadow-md: 0 4px 16px rgba(0, 0, 0, 0.15);
  --shadow-lg: 0 8px 24px rgba(0, 0, 0, 0.2);
  --transition: all 0.3s ease;
  --border-radius: 12px;
}

/* Reset y estilos base */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  color: var(--text-dark);
  line-height: 1.6;
  background-color: var(--bg-light);
}

/* Navegación */
.navbar {
  background-color: var(--primary-color) !important;
  padding: 1rem 0;
  transition: var(--transition);
}

.navbar.shadow-sm {
  box-shadow: var(--shadow-sm);
}

.navbar.scrolled {
  padding: 0.5rem 0;
  box-shadow: var(--shadow-md);
}

.navbar-brand-logo {
  display: inline-flex;
  align-items: center;
  margin-right: 1rem;
}

.logonav {
  width: 65px;
  height: 65px;
  object-fit: contain;
  transition: transform 0.3s ease;
}

.logonav:hover {
  transform: scale(1.1);
}

.navbar-brand {
  color: white !important;
  font-size: 1.5rem;
  font-weight: 700;
  letter-spacing: 0.5px;
}

.navbar .nav-link {
  color: white !important;
  font-size: 1.1rem;
  font-weight: 500;
  padding: 0.5rem 1rem !important;
  transition: var(--transition);
  position: relative;
}

.navbar .nav-link::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 50%;
  width: 0;
  height: 2px;
  background-color: white;
  transition: var(--transition);
  transform: translateX(-50%);
}

.navbar .nav-link:hover::after {
  width: 80%;
}

.navbar-toggler {
  border-color: white;
}

.navbar-toggler-icon {
  background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 30 30'%3e%3cpath stroke='rgba(255, 255, 255, 1)' stroke-linecap='round' stroke-miterlimit='10' stroke-width='2' d='M4 7h22M4 15h22M4 23h22'/%3e%3c/svg%3e");
}

/* Hero Section */
.hero-section {
  padding: 80px 0;
  background: linear-gradient(135deg, var(--bg-lighter) 0%, var(--bg-light) 100%);
  position: relative;
  overflow: hidden;
}

.hero-section::before {
  content: '';
  position: absolute;
  top: -50%;
  right: -10%;
  width: 500px;
  height: 500px;
  background: radial-gradient(circle, rgba(255, 55, 55, 0.1) 0%, transparent 70%);
  border-radius: 50%;
  animation: float 6s ease-in-out infinite;
}

@keyframes float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

.hero-title {
  font-size: 2.5rem;
  font-weight: 800;
  color: var(--primary-color);
  margin-bottom: 1rem;
  line-height: 1.2;
}

.hero-subtitle {
  font-size: 1.3rem;
  color: var(--text-light);
  margin-bottom: 1.5rem;
  font-weight: 500;
}

.hero-description {
  font-size: 1.1rem;
  color: var(--text-dark);
  margin-bottom: 1rem;
}

.hero-description p {
  margin-bottom: 1rem;
}

.hero-image img {
  width: 100%;
  height: auto;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.hero-image:hover img {
  transform: scale(1.02);
}

/* Sección de Estadísticas */
.stats-section {
  background: linear-gradient(135deg, var(--primary-color) 0%, #d62e2e 100%);
  color: white;
}

.stat-card {
  text-align: center;
  padding: 2rem 1rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: var(--border-radius);
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: var(--transition);
}

.stat-card:hover {
  transform: translateY(-10px);
  background: rgba(255, 255, 255, 0.15);
}

.stat-icon {
  font-size: 3rem;
  margin-bottom: 1rem;
  color: white;
}

.stat-number {
  font-size: 3.5rem;
  font-weight: 800;
  margin-bottom: 0.5rem;
  color: white;
}

.stat-label {
  font-size: 1.1rem;
  font-weight: 500;
  margin: 0;
  color: rgba(255, 255, 255, 0.9);
}

/* Sección Sobre Mí */
.about-section {
  background-color: white;
  position: relative;
}

.about-image-wrapper {
  position: relative;
}

.about-badge {
  position: absolute;
  bottom: 20px;
  left: 20px;
  background: var(--primary-color);
  color: white;
  padding: 1rem 1.5rem;
  border-radius: var(--border-radius);
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  box-shadow: var(--shadow-md);
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

.about-badge i {
  font-size: 1.5rem;
}

.about-text {
  font-size: 1.1rem;
  color: var(--text-light);
  margin-bottom: 2rem;
  line-height: 1.8;
}

.expertise-list {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.expertise-item {
  display: flex;
  align-items: center;
  gap: 1rem;
  font-size: 1.1rem;
  color: var(--text-dark);
}

.expertise-item i {
  color: var(--primary-color);
  font-size: 1.5rem;
}

/* Sección de Servicios */
.services-section {
  background-color: var(--bg-light);
  padding: 80px 0;
}

.section-title {
  font-size: 2.5rem;
  font-weight: 700;
  color: var(--primary-color);
  margin-bottom: 1rem;
}

.section-subtitle {
  font-size: 1.2rem;
  color: var(--text-light);
  max-width: 600px;
  margin: 0 auto;
}

/* Service Cards */
.service-card {
  background: white;
  border-radius: var(--border-radius);
  overflow: hidden;
  box-shadow: var(--shadow-sm);
  transition: var(--transition);
  height: 100%;
  display: flex;
  flex-direction: column;
}

.service-card:hover {
  transform: translateY(-8px);
  box-shadow: var(--shadow-lg);
}

.service-card-image {
  width: 100%;
  height: 250px;
  overflow: hidden;
  background-color: var(--bg-light);
  display: flex;
  align-items: center;
  justify-content: center;
}

.service-card-image img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  transition: transform 0.5s ease;
}

.service-card:hover .service-card-image img {
  transform: scale(1.1);
}

.service-card-body {
  padding: 1.5rem;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
}

.service-card-title {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--primary-color);
  margin-bottom: 1rem;
}

.service-card-text {
  color: var(--text-light);
  margin-bottom: 1rem;
  flex-grow: 1;
}

.service-price {
  display: flex;
  align-items: baseline;
  justify-content: center;
  gap: 0.5rem;
  padding: 1rem;
  margin-bottom: 1.5rem;
  background: linear-gradient(135deg, var(--bg-lighter) 0%, var(--bg-light) 100%);
  border-radius: 10px;
  border: 2px solid var(--primary-color);
}

.price-label {
  font-size: 0.9rem;
  color: var(--text-light);
  font-weight: 500;
}

.price-amount {
  font-size: 2rem;
  font-weight: 800;
  color: var(--primary-color);
  line-height: 1;
}

.price-currency {
  font-size: 1rem;
  color: var(--text-dark);
  font-weight: 600;
}

/* Sección de Testimonios */
.testimonials-section {
  background-color: white;
}

.testimonial-card {
  background: var(--bg-light);
  padding: 2rem;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-sm);
  transition: var(--transition);
  height: 100%;
}

.testimonial-card:hover {
  transform: translateY(-5px);
  box-shadow: var(--shadow-md);
}

.testimonial-stars {
  color: #FFD700;
  font-size: 1.2rem;
  margin-bottom: 1rem;
}

.testimonial-text {
  font-size: 1rem;
  color: var(--text-dark);
  margin-bottom: 1.5rem;
  font-style: italic;
  line-height: 1.6;
}

.testimonial-author {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  color: var(--primary-color);
}

.testimonial-author i {
  font-size: 2rem;
}

/* Sección de Contacto */
.contact-section {
  background: linear-gradient(135deg, var(--bg-lighter) 0%, var(--bg-light) 100%);
}

.contact-description {
  font-size: 1.1rem;
  color: var(--text-light);
  margin-bottom: 2rem;
}

.contact-info-list {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.contact-info-item {
  display: flex;
  align-items: flex-start;
  gap: 1rem;
  padding: 1rem;
  background: white;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-sm);
  transition: var(--transition);
}

.contact-info-item:hover {
  transform: translateX(10px);
  box-shadow: var(--shadow-md);
}

.contact-info-item i {
  font-size: 2rem;
  color: var(--primary-color);
  margin-top: 0.5rem;
}

.contact-info-item h4 {
  margin: 0 0 0.5rem 0;
  color: var(--text-dark);
  font-size: 1.1rem;
}

.contact-info-item a,
.contact-info-item p {
  margin: 0;
  color: var(--text-light);
  text-decoration: none;
  transition: var(--transition);
}

.contact-info-item a:hover {
  color: var(--primary-color);
}

.contact-form-wrapper {
  background: white;
  padding: 2rem;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-md);
}

.contact-form .form-label {
  font-weight: 600;
  color: var(--text-dark);
  margin-bottom: 0.5rem;
}

.contact-form .form-control {
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  padding: 0.75rem;
  transition: var(--transition);
}

.contact-form .form-control:focus {
  border-color: var(--primary-color);
  box-shadow: 0 0 0 0.2rem rgba(255, 55, 55, 0.1);
}

/* Botones */
.btn-primary {
  background-color: var(--primary-color);
  border: none;
  padding: 12px 30px;
  font-weight: 600;
  border-radius: 50px;
  transition: var(--transition);
  box-shadow: var(--shadow-sm);
}

.btn-primary:hover {
  background-color: var(--primary-hover);
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.btn-service {
  background-color: var(--primary-color);
  color: white;
  border: none;
  padding: 12px 24px;
  font-weight: 600;
  border-radius: 50px;
  transition: var(--transition);
  text-decoration: none;
  display: inline-block;
  width: 100%;
  text-align: center;
}

.btn-service:hover {
  background-color: var(--success-color);
  color: white;
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

/* Botones flotantes */
.whatsapp, .instagram-btn {
  position: fixed;
  width: 60px;
  height: 60px;
  right: 30px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 30px;
  z-index: 1000;
  box-shadow: var(--shadow-md);
  transition: var(--transition);
  text-decoration: none;
}

.whatsapp {
  bottom: 30px;
  background-color: #25d366;
  color: white;
}

.instagram-btn {
  bottom: 110px;
  background: linear-gradient(45deg, #f09433 0%, #e6683c 25%, #dc2743 50%, #cc2366 75%, #bc1888 100%);
  color: white;
}

.whatsapp:hover, .instagram-btn:hover {
  transform: scale(1.1);
  box-shadow: var(--shadow-lg);
  color: white;
}

/* Footer */
.footer {
  background-color: var(--primary-color);
  color: white;
  margin-top: 50px;
}

.footer p {
  margin: 0;
  font-size: 0.95rem;
}

.social-links {
  display: flex;
  gap: 20px;
  justify-content: center;
  align-items: center;
}

.social-links a {
  color: white;
  font-size: 1.8rem;
  transition: var(--transition);
}

.social-links a:hover {
  transform: scale(1.2);
  opacity: 0.8;
}

/* Responsive */
@media (max-width: 992px) {
  .hero-title {
    font-size: 2rem;
  }

  .hero-subtitle {
    font-size: 1.1rem;
  }

  .section-title {
    font-size: 2rem;
  }

  .navbar-brand {
    font-size: 1.2rem;
  }

  .stat-number {
    font-size: 2.5rem;
  }
}

@media (max-width: 768px) {
  .hero-section {
    padding: 40px 0;
  }

  .services-section {
    padding: 40px 0;
  }

  .hero-title {
    font-size: 1.75rem;
  }

  .service-card-image {
    height: 200px;
  }

  .whatsapp, .instagram-btn {
    width: 50px;
    height: 50px;
    font-size: 24px;
    right: 20px;
  }

  .whatsapp {
    bottom: 20px;
  }

  .instagram-btn {
    bottom: 85px;
  }

  .stat-number {
    font-size: 2rem;
  }

  .contact-form-wrapper {
    padding: 1.5rem;
  }
}

@media (max-width: 576px) {
  .hero-title {
    font-size: 1.5rem;
  }

  .hero-subtitle {
    font-size: 1rem;
  }

  .section-title {
    font-size: 1.75rem;
  }

  .hero-description {
    font-size: 1rem;
  }

  .about-badge {
    position: static;
    margin-top: 1rem;
  }
}
